  // Set state
  onFileChange = (e) => {
    let files = [...this.state.files]
    files[e.target.dataset.id][e.target.className] = e.target.files[0]
    this.setState({[e.target.name]: e.target.files[0]})
  };

  onTimeChange = (e, value, dateString) => {
    let files = [...this.state.files]
    files[e.target.dataset.id][e.target.className] = dateString
    this.setState({[e.target.name]: dateString})
  }

  handleChange(e) {
    if (["longitude", "latitude", "altitude", "satellite", "frequency", "rate"].includes(e.target.name)) {
      let files = [...this.state.files]
      files[e.target.dataset.id][e.target.name] = e.target.value
      this.setState({[e.target.name]: e.target.value})
    } else {
      this.setState({[e.target.name]: e.target.value})
    }
  }

  addFile = (e) => {
    this.setState((prevState) => ({
      files: [...prevState.files, {
        selectedFile: null,
        longitude: 0,
        latitude: 0,
        altitude: 0,
        satellite: "",
        frequency: 0,
        rate: 0,
        time: "",
      }]
    }));
  }

