import axios from 'axios';

let jwtToken = localStorage.getItem("authorization");

class UserService {

  createUser(user){
    return axios.post("http://localhost:8080/signup", user);
  }

  signin(user){
    return axios.post("http://localhost:8080/signin" ,user);
  }

  getUserDetail(username){
    axios.interceptors.request.use(
      function(config) {
        if (jwtToken) {
          config.headers["authorization"] = "Bearer " + jwtToken;
        }
        return config;
      },
      function(err) {
        return Promise.reject(err);
      }
    );
    // const options = {
    //   headers: {
    //     'Authorization': 'Bearer ' + jwt,
    //     "Content-Type": "application/json",
    //     "Access-Control-Allow-Origin": "*",
    //     "Access-Control-Allow-Methods": "DELETE, POST, GET, OPTIONS",
    //     "Access-Control-Allow-Headers": "Content-Type, Access-Control-Allow-Headers, Authorization, X-Requested-With",
    //     'Access-Control-Allow-Credentials' : 'true'
    //   }
    // };
    // return axios.post("http://localhost:8080/user-detail", {"username": username}, options);
    return axios.post("http://localhost:8080/user-detail", {"username": username});
  }
}

export default new UserService()
