import React, { Component } from 'react';
/* CSS */
import '.././css/signinpage.css';
import 'bootstrap/dist/css/bootstrap.min.css';
/* React-Bootstrap Components */
import { Navbar, Nav } from 'react-bootstrap';
import { Form, Button, Image} from 'react-bootstrap';

import UserService from '.././services/UserService.js';

class signinpage extends Component{
  constructor(props) {
    super(props);

    this.state = {
        username: "",
        password: "",
    }

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleSubmit = async (event) => {
     let user = {
       username: this.state.username,
       password: this.state.password,
     }
     console.log("can sign in..." + JSON.stringify(user));
  }

  handleChange(e) {
    this.setState({ [e.target.name] : e.target.value });
  }

  render(){
    return (
      <div>
        <section id='navbar'>
          <Navbar fixed="top" expand="lg" bg="dark" variant="dark">
            <Navbar.Brand className="navbar-brand">Quasar</Navbar.Brand>
            <Navbar.Toggle />
            <Navbar.Collapse className="justify-content-end">
              <Nav>
                <Nav.Link href="/">Home</Nav.Link>
                <Nav.Link href="/join/signup">Sign up</Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Navbar>
        </section>

        <section id="signinform">
          <Form className="form-signin">
            <Image className="profile-img" src="https://images.hdrsoft.com/images/lighthouse/thumbs/hdr-vibrant.jpg" roundedCircle />
            <h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
            <Form.Group controlId="formGroupEmail">
              <Form.Control
                type="text"
                placeholder="Username"
                value = {this.state.username}
                onChange={this.handleChange}
              />
            </Form.Group>
            <Form.Group controlId="formGroupPassword">
              <Form.Control
                type="password"
                placeholder="Password"
                value = {this.state.password}
                onChange={this.handleChange}
              />
            </Form.Group>
            <Form.Check label="Remember me" />
            <Button className="form-btn" variant="dark" type="submit" size="lg">
              Sign in
            </Button>
          </Form>
        </section>

        <footer id="footer">
          <div className="container-fluid">
            <p>© Copyright 2020 WiTech Lab</p>
            <p>Carnegie Mellon University</p>
          </div>
        </footer>
      </div>
    );
  }
}

export default signinpage;
