import React, { Component } from 'react';
/* CSS */
import '.././css/signuppage.css';
import 'bootstrap/dist/css/bootstrap.min.css';
/* React-Bootstrap Components */
import { Navbar, Nav } from 'react-bootstrap';
import { Form, Row, Col} from 'react-bootstrap';
import {Button as SubmitButton} from 'react-bootstrap'

import { Upload, Button } from 'antd';
import { UploadOutlined } from '@ant-design/icons';

const fileList = [];

class signuppage extends Component{
  constructor(props) {
    super(props);

    this.state = {
        username: "",
        email: "",
        password: "",
        location: "",
        validated: false
    }

    this.changeUsernameHandler = this.changeUsernameHandler.bind(this);
    this.changeEmailHandler = this.changeEmailHandler.bind(this);
    this.changePasswordHandler = this.changePasswordHandler.bind(this);
    this.changeLocationHandler = this.changeLocationHandler.bind(this);
    this.saveUser = this.saveUser.bind(this);
  }

  handleSubmit = (event) => {
      const form = event.currentTarget;
      if (form.checkValidity() === false) {
        event.preventDefault();
        event.stopPropagation();
      }

      this.setState({validated: true});
  };

  saveUser = event => {
    event.preventDefault();

    let user = {
      username: this.state.username,
      email: this.state.email,
      password: this.state.password,
      location:this.state.location
    }
    console.log(JSON.stringify(user));
    }

  changeUsernameHandler = event => {
    this.setState({username: event.target.value});
  }

  changeEmailHandler = event => {
    this.setState({email: event.target.value});
  }

  changePasswordHandler = event => {
    this.setState({password: event.target.value});
  }

  changeLocationHandler = event => {
    this.setState({location: event.target.value});
  }

  render(){
    return (
      <div>
        <section id='navbar'>
          <Navbar fixed="top" expand="lg" bg="dark" variant="dark">
            <Navbar.Brand className="navbar-brand">Quasar</Navbar.Brand>
            <Navbar.Toggle />
            <Navbar.Collapse className="justify-content-end">
              <Nav>
                <Nav.Link href="/">Home</Nav.Link>
                <Nav.Link href="/join/signin">Sign in</Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Navbar>
        </section>

        <section id="signupform">
          <Form noValidate validated={this.state.validated} onSubmit={this.handleSubmit} className="form-signup">
            <Row>
              <Col lg={6} className="center">

                <h5>Choose a photo</h5>
                <Upload
                  action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                  listType="picture"
                  defaultFileList={[...fileList]}
                >
                  <Button icon={<UploadOutlined />}>Upload</Button>
                </Upload>

              </Col>

              <Col lg={6}>

                <Form.Group className="left">
                  <Form.Label>Username</Form.Label>
                  <Form.Control
                    placeholder="Username"
                    value={this.state.username}
                    onChange={this.changeUsernameHandler}
                    required
                  />
                  <Form.Control.Feedback type="invalid">
                    Please provide a valid username.
                  </Form.Control.Feedback>
                </Form.Group>

                <Form.Group className="left" controlId="formBasicEmail">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    placeholder="Enter email"
                    value={this.state.email}
                    onChange={this.changeEmailHandler}
                    required
                    pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$"
                  />
                  <Form.Control.Feedback type="invalid">
                    Please provide a valid email.
                  </Form.Control.Feedback>
                </Form.Group>

                <Form.Group className="left"  controlId="formGridPassword1">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Enter password"
                    value={this.state.password}
                    onChange={this.changePasswordHandler}
                    required
                    minLength="3"
                  />
                  <Form.Control.Feedback type="invalid">
                    Please provide a valid password.
                  </Form.Control.Feedback>
                </Form.Group>

                <Form.Group className="left">
                  <Form.Label>Location</Form.Label>
                  <Form.Control
                    placeholder="Location"
                    value={this.state.location}
                    onChange={this.changeLocationHandler}
                    required
                  />
                  <Form.Control.Feedback type="invalid">
                    Please provide a valid location.
                  </Form.Control.Feedback>
                </Form.Group>

                <SubmitButton
                  className="form-btn"
                  variant="dark"
                  type="submit"
                  size="lg"
                  onClick={this.saveUser}
                >
                  Sign up
                </SubmitButton>
              </Col>
            </Row>
          </Form>
        </section>

        <footer id="footer">
          <div className="container-fluid">
            <p>Â© Copyright 2020 WiTech Lab</p>
            <p>Carnegie Mellon University</p>
          </div>
        </footer>
      </div>
    );
  }
}

export default signuppage;
